import axios from "axios";
import React, { useEffect, useState } from "react";
import {useNavigate} from "react-router-dom"
import { goToPokedex } from "../../routes/coordinator";
import PokemonCard from "../PokemonCard/PokemonCard";


const Home = () => {

    const navigate = useNavigate()

    const [pokemons, setPokemons] = useState([])
    const [pokemonNames, setPokemonNames] = useState([])

    {useEffect(() => {
        getPokemonNames();
      }, []);}

    useEffect(() => {
        const newList = [];
        pokemonNames.forEach((item) => {
          axios.get(`https://pokeapi.co/api/v2/pokemon/${item.name}`)
            .then((response) => {
              newList.push(response.data);
              setPokemons(newList)
              console.log(response.data)
            //   if (newList.length === 20) {
            //     const orderedList = newList.sort((a, b) => {
            //       return a.id - b.id;
            //     });
            //     setPokemons(orderedList);
            //   }
            })
            .catch((error) => alert(error));
        });
      }, [pokemonNames]);
    
      const getPokemonNames = () => {
        axios.get(`https://pokeapi.co/api/v2/pokemon?limit=30`)
          .then((response) => {
            setPokemonNames(response.data.results);
          })
          .catch((error) => alert(error));
      };
    
    //   const data = {
    //     pokemons,
    //     setPokemons,
    //     pokedex,
    //     setPokedex
    //   };
    
    return(

        <div>
            <header>
                <button onClick={() => goToPokedex(navigate)}>Ver Pokedex</button>
                <h1>Lista de Pok√©mons</h1>
            </header>
            <div>
                {pokemons && pokemons.map((poke) => {
                    return (
                        <PokemonCard key={poke.name} poke={poke}  />
                    )
                })}
                
            </div>
        </div>
    )
}

export default Home;
